{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Creativity Generator Agent Output",
  "type": "object",
  "additionalProperties": false,
  "properties": {
    "divergentAxes": {
      "default": [],
      "description": "Short labels describing how the idea seeds explore different directions.",
      "examples": [
        [
          "Economy risk-reward",
          "Discard loop disruption",
          "Endless scaling cap"
        ]
      ],
      "items": {
        "type": "string"
      },
      "type": "array"
    },
    "ideaSeeds": {
      "default": [],
      "description": "Concept seeds ready for downstream analysis.",
      "items": {
        "additionalProperties": false,
        "properties": {
          "conceptPitch": {
            "description": "One-paragraph pitch explaining the core gameplay hook.",
            "examples": [
              "Reward late-hand discards by caching unused mult for the boss blind."
            ],
            "type": "string"
          },
          "expectedPlaystyle": {
            "description": "How the player will pilot the deck when this joker is active.",
            "examples": [
              "Aggressive discard cycling with selective hand banking."
            ],
            "type": "string"
          },
          "handoffNotes": {
            "description": "Instructions or questions for downstream specialists.",
            "examples": [
              "Check balance impact if mult cache scales into endless."
            ],
            "type": "string"
          },
          "ideaId": {
            "description": "Stable identifier used across the design workflow.",
            "examples": [
              "IDEA-01"
            ],
            "type": "string"
          },
          "noveltyHook": {
            "description": "What makes this concept distinct from existing jokers.",
            "examples": [
              "Creates a delayed scoring window tied to discard restraint."
            ],
            "type": "string"
          },
          "primaryMechanics": {
            "default": [],
            "description": "Key mechanics or triggers leveraged by the concept.",
            "examples": [
              [
                "discard",
                "boss blind",
                "mult cache"
              ]
            ],
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "referenceJokers": {
            "default": [],
            "description": "Existing jokers or effects serving as inspiration or contrast.",
            "examples": [
              [
                "Gros Michel",
                "Banner"
              ]
            ],
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "riskFlags": {
            "default": [],
            "description": "Potential risks or unknowns to investigate.",
            "examples": [
              [
                "Mult cache might stack with Gros Michel for runaway scoring"
              ]
            ],
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "targetRarity": {
            "description": "Suggested rarity to match power level and availability.",
            "enum": [
              "common",
              "uncommon",
              "rare",
              "legendary"
            ],
            "examples": [
              "rare"
            ],
            "type": "string"
          },
          "title": {
            "description": "Working name for the concept seed.",
            "examples": [
              "Cache King"
            ],
            "type": "string"
          }
        },
        "required": [
          "conceptPitch",
          "expectedPlaystyle",
          "handoffNotes",
          "ideaId",
          "noveltyHook",
          "primaryMechanics",
          "referenceJokers",
          "riskFlags",
          "targetRarity",
          "title"
        ],
        "type": "object"
      },
      "type": "array"
    },
    "ideationSummary": {
      "description": "Narrative summary of the opportunity space and recommended focus.",
      "examples": [
        "Discard control has openings for delayed mult payouts without breaking ante pacing."
      ],
      "type": "string"
    },
    "inspirationSources": {
      "default": [],
      "description": "Artifacts, research, or existing jokers referenced during ideation.",
      "examples": [
        [
          {
            "description": "Banner highlights discard reward patterns relevant to Cache King.",
            "link": "co_game_designer_agent_specs/all_jokers.md#22",
            "sourceId": "joker-banner",
            "type": "joker"
          }
        ]
      ],
      "items": {
        "additionalProperties": false,
        "properties": {
          "description": {
            "description": "Why the source matters for this concept.",
            "examples": [
              "Week 31 metagame notes show discard decks underperforming."
            ],
            "type": "string"
          },
          "link": {
            "description": "Optional path or URL for deeper reference.",
            "examples": [
              "experiments/discard_meta/week31.md"
            ],
            "type": "string"
          },
          "sourceId": {
            "description": "Unique identifier for the source.",
            "examples": [
              "joker-banner"
            ],
            "type": "string"
          },
          "type": {
            "description": "Category of the source such as joker, research, or anecdote.",
            "examples": [
              "joker"
            ],
            "type": "string"
          }
        },
        "required": [
          "description",
          "sourceId",
          "type"
        ],
        "type": "object"
      },
      "type": "array"
    },
    "openQuestions": {
      "default": [],
      "description": "Clarifications or decisions required before moving forward.",
      "examples": [
        [
          "Confirm if endless scaling is allowed for discard archetypes."
        ]
      ],
      "items": {
        "type": "string"
      },
      "type": "array"
    }
  },
  "required": [
    "divergentAxes",
    "ideaSeeds",
    "ideationSummary",
    "inspirationSources",
    "openQuestions"
  ]
}

{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Joker Synergy Specialist Agent Output",
  "type": "object",
  "additionalProperties": false,
  "properties": {
    "globalObservations": {
      "default": [],
      "description": "High-level notes about the joker concept and metagame context.",
      "examples": [
        "Discard-focused decks lack payoff unless paired with piston jokers."
      ],
      "items": {
        "type": "string"
      },
      "type": "array"
    },
    "risks": {
      "default": [],
      "description": "Potential pitfalls or incompatibilities discovered during the analysis.",
      "items": {
        "additionalProperties": false,
        "properties": {
          "description": {
            "description": "What could go wrong if the package is adopted.",
            "examples": [
              "Stacking with Madness joker doubles the multiplier curve."
            ],
            "type": "string"
          },
          "mitigation": {
            "description": "Suggested guardrail or monitoring plan.",
            "examples": [
              "Cap base chips at 40 when paired with Madness."
            ],
            "type": "string"
          },
          "riskId": {
            "description": "Identifier for cross-agent traceability.",
            "examples": [
              "risk-madness-overlap"
            ],
            "type": "string"
          },
          "severity": {
            "description": "Relative severity label.",
            "enum": [
              "low",
              "medium",
              "high"
            ],
            "examples": [
              "high"
            ],
            "type": "string"
          }
        },
        "required": [
          "description",
          "mitigation",
          "riskId",
          "severity"
        ],
        "type": "object"
      },
      "type": "array"
    },
    "summary": {
      "description": "One-paragraph synthesis of the joker synergy landscape.",
      "examples": [
        "Discard engines gain the most, provided economy levers offset the multiplier spike."
      ],
      "type": "string"
    },
    "supportingResearch": {
      "default": [],
      "description": "Pointers to experiments, spreadsheets, or references used in the analysis.",
      "items": {
        "additionalProperties": false,
        "properties": {
          "description": {
            "description": "Why the source matters for this recommendation.",
            "examples": [
              "Win-rate logs from week 34 patch."
            ],
            "type": "string"
          },
          "link": {
            "description": "Optional URL or file path if accessible.",
            "examples": [
              "experiments/synergy_runs/week34.md"
            ],
            "type": "string"
          },
          "sourceId": {
            "description": "Unique identifier for the research artifact.",
            "examples": [
              "exp-34-discard-logs"
            ],
            "type": "string"
          }
        },
        "required": [
          "description",
          "sourceId"
        ],
        "type": "object"
      },
      "type": "array"
    },
    "synergyPackages": {
      "default": [],
      "description": "Ranked synergy packages with quantitative expectations.",
      "items": {
        "additionalProperties": false,
        "properties": {
          "description": {
            "description": "How the synergy behaves and what gameplay it unlocks.",
            "examples": [
              "Chain discards with Flush House to convert draw cycles into chip bursts."
            ],
            "type": "string"
          },
          "enablingCards": {
            "default": [],
            "description": "Cards, jokers, or consumables required to realize the package.",
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "expectedChips": {
            "description": "Expected chip gain by ante 6 under optimal play.",
            "examples": [
              280
            ],
            "minimum": 0,
            "type": "number"
          },
          "expectedMultiplier": {
            "description": "Expected multiplier value by ante 6.",
            "examples": [
              18
            ],
            "minimum": 0,
            "type": "number"
          },
          "name": {
            "description": "Short name for the synergy package.",
            "examples": [
              "Discard Dynamo"
            ],
            "type": "string"
          },
          "packageId": {
            "description": "Stable identifier used by the orchestrator when referencing this package.",
            "examples": [
              "SP-02"
            ],
            "type": "string"
          },
          "rarityFit": {
            "description": "Best-fit joker rarity for balance.",
            "enum": [
              "common",
              "uncommon",
              "rare",
              "legendary"
            ],
            "examples": [
              "rare"
            ],
            "type": "string"
          },
          "setupSequence": {
            "default": [],
            "description": "Ordered steps or milestones to assemble the synergy.",
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "tuningNotes": {
            "description": "Suggestions to tweak numbers or effects for this package.",
            "examples": [
              "Limit joker activation to once per round."
            ],
            "type": "string"
          }
        },
        "required": [
          "description",
          "enablingCards",
          "expectedChips",
          "expectedMultiplier",
          "name",
          "packageId",
          "rarityFit",
          "setupSequence",
          "tuningNotes"
        ],
        "type": "object"
      },
      "type": "array"
    }
  },
  "required": [
    "globalObservations",
    "risks",
    "summary",
    "supportingResearch",
    "synergyPackages"
  ]
}

{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Run Balance Simulator Agent Output",
  "type": "object",
  "additionalProperties": false,
  "properties": {
    "balanceVerdict": {
      "description": "Overall statement about balance health across simulated runs.",
      "examples": [
        "Playable with mitigation; busted seeds exceed multiplier targets by ante 7."
      ],
      "type": "string"
    },
    "mitigationIdeas": {
      "default": [],
      "description": "Recommended levers or follow-up actions based on simulation findings.",
      "examples": [
        "Add escalating shop cost after first purchase"
      ],
      "items": {
        "type": "string"
      },
      "type": "array"
    },
    "scenarioReports": {
      "default": [],
      "description": "Per-scenario metrics and risk summaries.",
      "items": {
        "additionalProperties": false,
        "properties": {
          "anteProgression": {
            "default": [],
            "description": "Key milestones or breakpoints observed by ante.",
            "examples": [
              [
                {
                  "ante": 2,
                  "note": "Economy stable"
                },
                {
                  "ante": 5,
                  "note": "Chip spike begins"
                }
              ]
            ],
            "items": {
              "additionalProperties": false,
              "properties": {
                "ante": {
                  "description": "Ante number for the observation.",
                  "examples": [
                    5
                  ],
                  "minimum": 1,
                  "type": "integer"
                },
                "note": {
                  "description": "What happens at this ante milestone.",
                  "examples": [
                    "Player reliably clears blinds with 2 hands."
                  ],
                  "type": "string"
                }
              },
              "required": [
                "ante",
                "note"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "chipTrajectory": {
            "default": [],
            "description": "Estimated chip outputs per ante for the scenario.",
            "examples": [
              [
                {
                  "ante": 3,
                  "chips": 120
                },
                {
                  "ante": 6,
                  "chips": 320
                }
              ]
            ],
            "items": {
              "additionalProperties": false,
              "properties": {
                "ante": {
                  "description": "Ante at which the chip estimate applies.",
                  "examples": [
                    6
                  ],
                  "minimum": 1,
                  "type": "integer"
                },
                "chips": {
                  "description": "Estimated chip output for a typical winning hand.",
                  "examples": [
                    280
                  ],
                  "minimum": 0,
                  "type": "number"
                }
              },
              "required": [
                "ante",
                "chips"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "economySummary": {
            "description": "Overview of money flow, shop purchases, and scarcity pressures.",
            "examples": [
              "Baseline survives with 12$ buffer; busted run prints infinite cash via coupon loop."
            ],
            "type": "string"
          },
          "multiplierTrajectory": {
            "default": [],
            "description": "Estimated multiplier values per ante.",
            "examples": [
              [
                {
                  "ante": 4,
                  "multiplier": 9
                },
                {
                  "ante": 7,
                  "multiplier": 22
                }
              ]
            ],
            "items": {
              "additionalProperties": false,
              "properties": {
                "ante": {
                  "description": "Ante at which the multiplier applies.",
                  "examples": [
                    7
                  ],
                  "minimum": 1,
                  "type": "integer"
                },
                "multiplier": {
                  "description": "Estimated multiplier for the scenario.",
                  "examples": [
                    18
                  ],
                  "minimum": 0,
                  "type": "number"
                }
              },
              "required": [
                "ante",
                "multiplier"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "probabilityEstimate": {
            "description": "Rough likelihood of assembling the scenario conditions.",
            "examples": [
              "moderate"
            ],
            "enum": [
              "low",
              "moderate",
              "high"
            ],
            "type": "string"
          },
          "riskNotes": {
            "default": [],
            "description": "Risks or anomalies observed in the scenario.",
            "examples": [
              [
                "Multiplier snowballs if Madness also present"
              ]
            ],
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "scenarioName": {
            "description": "Descriptor for the simulated scenario.",
            "examples": [
              "Baseline discard deck"
            ],
            "type": "string"
          }
        },
        "required": [
          "anteProgression",
          "chipTrajectory",
          "economySummary",
          "multiplierTrajectory",
          "probabilityEstimate",
          "riskNotes",
          "scenarioName"
        ],
        "type": "object"
      },
      "type": "array"
    },
    "simulationMetadata": {
      "description": "Assumptions, tool versions, and data sources underpinning the simulation.",
      "examples": [
        {
          "assumptions": [
            "Player uses optimal discard lines",
            "Shop offers average rarity distribution"
          ],
          "rngSeeds": [
            "baseline-seed-103",
            "synergy-seed-227",
            "busted-seed-404"
          ],
          "tools": [
            "BALATRO_RUN_SIM v1.2"
          ]
        }
      ],
      "properties": {
        "assumptions": {
          "default": [],
          "description": "Key assumptions that affect interpretation of the results.",
          "items": {
            "type": "string"
          },
          "type": "array"
        },
        "rngSeeds": {
          "default": [],
          "description": "Named seeds or RNG notes for reproducibility.",
          "items": {
            "type": "string"
          },
          "type": "array"
        },
        "tools": {
          "default": [],
          "description": "Simulation tools or scripts used to produce the report.",
          "items": {
            "type": "string"
          },
          "type": "array"
        }
      },
      "required": [
        "assumptions",
        "rngSeeds",
        "tools"
      ],
      "type": "object"
    },
    "summary": {
      "description": "Concise narration of what the simulations revealed.",
      "examples": [
        "Synergy-heavy runs demand higher rarity gate; busted loops require cost scaling."
      ],
      "type": "string"
    }
  },
  "required": [
    "balanceVerdict",
    "mitigationIdeas",
    "scenarioReports",
    "simulationMetadata",
    "summary"
  ]
}

{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Design Conflict Verifier Agent Output",
  "type": "object",
  "additionalProperties": false,
  "properties": {
    "blockedItems": {
      "default": [],
      "description": "Identifiers for concepts or spec elements that cannot advance without changes.",
      "examples": [
        [
          "IDEA-01",
          "jokerSpec"
        ]
      ],
      "items": {
        "type": "string"
      },
      "type": "array"
    },
    "conflictFindings": {
      "default": [],
      "description": "Detailed conflicts discovered during verification.",
      "items": {
        "additionalProperties": false,
        "properties": {
          "conflictType": {
            "description": "Category of the conflict encountered.",
            "enum": [
              "rules",
              "economy",
              "balance",
              "ux",
              "technical"
            ],
            "examples": [
              "rules"
            ],
            "type": "string"
          },
          "description": {
            "description": "Narrative explanation of the conflict.",
            "examples": [
              "Effect duplicates Banner and stacks with Gros Michel for runaway discards."
            ],
            "type": "string"
          },
          "findingId": {
            "description": "Stable identifier for the finding.",
            "examples": [
              "conflict-rules-01"
            ],
            "type": "string"
          },
          "impactedSystems": {
            "default": [],
            "description": "Systems or mechanics affected by the issue.",
            "examples": [
              [
                "discard economy",
                "boss blind scaling"
              ]
            ],
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "resolutionRecommendation": {
            "description": "Proposed mitigation or follow-up action.",
            "examples": [
              "Limit discard bonus to once per blind or move to legendary rarity."
            ],
            "type": "string"
          },
          "severity": {
            "description": "Relative severity of the conflict.",
            "enum": [
              "low",
              "medium",
              "high"
            ],
            "examples": [
              "high"
            ],
            "type": "string"
          },
          "status": {
            "description": "Current disposition of the finding.",
            "enum": [
              "blocked",
              "mitigated",
              "monitor"
            ],
            "examples": [
              "blocked"
            ],
            "type": "string"
          }
        },
        "required": [
          "conflictType",
          "description",
          "findingId",
          "impactedSystems",
          "resolutionRecommendation",
          "severity",
          "status"
        ],
        "type": "object"
      },
      "type": "array"
    },
    "policyChecklist": {
      "default": [],
      "description": "Standard checks covering rules, economy, rarity, and UX guarantees.",
      "items": {
        "additionalProperties": false,
        "properties": {
          "checkId": {
            "description": "Identifier for the checklist item.",
            "examples": [
              "score-math"
            ],
            "type": "string"
          },
          "description": {
            "description": "What the checklist item validates.",
            "examples": [
              "Score multipliers stay within ante 8 caps."
            ],
            "type": "string"
          },
          "notes": {
            "description": "Supporting evidence or reasoning for the status.",
            "examples": [
              "Simulator confirms multiplier stays under 25 at ante 6."
            ],
            "type": "string"
          },
          "status": {
            "description": "Outcome of the checklist evaluation.",
            "enum": [
              "pass",
              "fail",
              "attention"
            ],
            "examples": [
              "attention"
            ],
            "type": "string"
          }
        },
        "required": [
          "checkId",
          "description",
          "notes",
          "status"
        ],
        "type": "object"
      },
      "type": "array"
    },
    "validatedAspects": {
      "default": [],
      "description": "Elements of the design confirmed safe to advance.",
      "examples": [
        [
          {
            "aspectId": "economy-price",
            "description": "Shop price aligns with rarity benchmarks.",
            "notes": "Matches rare joker baseline plus 2 for scaling risk."
          }
        ]
      ],
      "items": {
        "additionalProperties": false,
        "properties": {
          "aspectId": {
            "description": "Identifier for the validated aspect.",
            "examples": [
              "economy-price"
            ],
            "type": "string"
          },
          "description": {
            "description": "Summary of what passed verification.",
            "examples": [
              "Effect respects discard limit per blind."
            ],
            "type": "string"
          },
          "notes": {
            "description": "Supporting rationale or evidence.",
            "examples": [
              "Synergy sim shows no infinite loop when paired with Banner."
            ],
            "type": "string"
          }
        },
        "required": [
          "aspectId",
          "description",
          "notes"
        ],
        "type": "object"
      },
      "type": "array"
    },
    "validationNotes": {
      "default": [],
      "description": "Additional context or references gathered during verification.",
      "examples": [
        [
          "Cross-checked against Joker #22 Banner for discard overlap."
        ]
      ],
      "items": {
        "type": "string"
      },
      "type": "array"
    },
    "verificationSummary": {
      "description": "Top-level verdict on whether the design may proceed.",
      "examples": [
        "Blocked pending rarity increase and discard trigger cap."
      ],
      "type": "string"
    }
  },
  "required": [
    "blockedItems",
    "conflictFindings",
    "policyChecklist",
    "validatedAspects",
    "validationNotes",
    "verificationSummary"
  ]
}

{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "Lead Designer Orchestrator Agent Output",
  "type": "object",
  "additionalProperties": false,
  "properties": {
    "balancePlan": {
      "description": "Summary of balance adjustments and mitigations coordinated with the simulator findings.",
      "examples": [
        {
          "globalVerdict": "Keep joker rare; cap multiplier growth by ante 6.",
          "mitigations": [
            "Gate effect behind discard cost scaling",
            "Increase shop price by 2x baseline"
          ],
          "rationale": "Simulator flagged runaway econ in busted seed."
        }
      ],
      "properties": {
        "globalVerdict": {
          "description": "Single sentence verdict on balance viability.",
          "examples": [
            "Balanced with mitigation applied to econ scaling."
          ],
          "type": "string"
        },
        "mitigations": {
          "default": [],
          "description": "Concrete balance levers to apply or monitor.",
          "items": {
            "type": "string"
          },
          "type": "array"
        },
        "rationale": {
          "description": "Key simulator evidence supporting the verdict.",
          "examples": [
            "Baseline run safe; busted run needs ante 5 cap."
          ],
          "type": "string"
        }
      },
      "required": [
        "globalVerdict",
        "mitigations",
        "rationale"
      ],
      "type": "object"
    },
    "conceptPlan": {
      "description": "How the orchestrator curates idea seeds from the creativity generator.",
      "properties": {
        "divergentAxes": {
          "default": [],
          "description": "Axes from the generator output that informed selection decisions.",
          "examples": [
            [
              "Discard tempo",
              "Economy burst",
              "Scaling restraint"
            ]
          ],
          "items": {
            "type": "string"
          },
          "type": "array"
        },
        "notes": {
